sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

data.changeFields = [];

var grChange = new GlideRecord('change_request');
grChange.addEncodedQuery(options.query);
grChange.setLimit(options.limit);
grChange.query();


while (grChange.next()) {
	var obj ={

	 number: grChange.getDisplayValue('number'),
   priority: grChange.getDisplayValue('priority'),
   short_description:  grChange.getValue('short_description'),
   impact: grChange.getDisplayValue('impact'),
	 type: grChange.getDisplayValue('type'),


	}
   data.changeFields.push(obj);
}


})();
