sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.assigntsIncidents = [];

var grIncident = new GlideRecord('incident');
grIncident.addEncodedQuery(options.query);
grIncident.setLimit(options.limit);
grIncident.query();


while (grIncident.next()) {
	var obj ={

	 number: grIncident.getDisplayValue('number'),
   priority: grIncident.getDisplayValue('priority'),
   short_description:  grIncident.getValue('short_description'),
   state: grIncident.getDisplayValue('state'),

	}
   data.assigntsIncidents.push(obj);
}


})();
